(()=>{"use strict";class t{constructor(){this.navLinks=document.querySelectorAll(".nav-link"),this.sections=document.querySelectorAll(".section"),this.init()}init(){this.navLinks.forEach(t=>{t.addEventListener("click",e=>{e.preventDefault();const o=t.getAttribute("href")?.substring(1);o&&(this.scrollToSection(o),this.setActiveLink(t))})});const t=document.querySelector(".content");t&&t.addEventListener("scroll",()=>this.updateActiveNavOnScroll())}scrollToSection(t){const e=document.getElementById(t),o=document.querySelector(".content");if(e&&o){const t=e.offsetTop-o.offsetTop;o.scrollTo({top:t,behavior:"smooth"})}}setActiveLink(t){this.navLinks.forEach(t=>t.classList.remove("active")),t.classList.add("active")}updateActiveNavOnScroll(){const t=document.querySelector(".content");if(!t)return;const e=t.scrollTop+100;this.sections.forEach(o=>{const s=o.offsetTop-t.offsetTop,c=o.offsetHeight,n=o.getAttribute("id");e>=s&&e<s+c&&this.navLinks.forEach(t=>{t.classList.remove("active"),t.getAttribute("href")===`#${n}`&&t.classList.add("active")})})}}document.addEventListener("DOMContentLoaded",()=>{new t})})();